import './App.css';
import { useEffect, useState } from "react";
import ContactForm from "./components/ContactForm";


function App() {
  const [usersData, setUsersData] = useState([]);
  const [showContactList, setShowContactList] = useState(false);
  const [selectedContact, setSelectedContact] = useState(null);
  const [contactDetails, setContactDetails] = useState(null);
  const [contacts, setContacts] = useState([]);



   useEffect(() => {
    getData();
  }, []);

  async function getData() {
    const response = await fetch('https://jsonplaceholder.typicode.com/users')
    const json = await response.json();
    setUsersData(json);
    console.log(json);
  }


  const toggleContactList = () => {
    setShowContactList(!showContactList);
  };

  const viewContactDetails = (contact) => {
    setSelectedContact(contact);
  };


  const createContact = (newContact) => {
    setContacts([...contacts, newContact]);
  };


  return (
    <div className="App">
      <h1>Menu</h1>
      <a href="#" onClick={toggleContactList}>
        {showContactList ? "Hide Contact List" : "Show Contact List"}
      </a>
      {showContactList && (

      <ul>
        {usersData.map((user) => (
          <li key={user.id}>
            <a href="#" onClick={() => viewContactDetails(user)}>
              View 
              </a>
            {user.name} 
          </li>
        ))}
      </ul>
      )}
        {selectedContact && (
        <div>
          <h2>Contact Details</h2>
          <p>Name: {selectedContact.name}</p>

          <p>Street: {selectedContact.address.street}</p>
          <p>City: {selectedContact.address.city}</p>

        </div>
      )}


   <ContactForm onCreateContact={createContact} />
    </div>
  );
}

export default App;
