import './App.css';
import { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";

function ContactList({ usersData, viewContactDetails }) {
  return (
    <ul>
      {usersData.map((user) => (
        <li key={user.id}>
          <Link to={`/contacts/${user.id}`}>View</Link>
          {user.name}
        </li>
      ))}
    </ul>
  );
}

function ContactDetails({ selectedContact, closeContactDetails }) {
  return (
    <div>
      <h2>Contact Details</h2>
      <p>Name: {selectedContact.name}</p>
      <p>Street: {selectedContact.address.street}</p>
      <p>City: {selectedContact.address.city}</p>
      <Link to="/contacts">Back to Contact List</Link>
    </div>
  );
}

function CreateContact({ createContact }) {
  // Your create contact form here
}

function App() {
  const [usersData, setUsersData] = useState([]);
  const [selectedContact, setSelectedContact] = useState(null);

  useEffect(() => {
    getData();
  }, []);

  async function getData() {
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/users');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setUsersData(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  }

  const viewContactDetails = (contact) => {
    setSelectedContact(contact);
  };

  const closeContactDetails = () => {
    setSelectedContact(null);
  };

  const createContact = async (newContactData) => {
    // Implement your create contact logic here
  };

  return (
    <Router>
      <div className="App">
        <h1>Menu</h1>
        <div>
          <Link to="/contacts">Show Contact List</Link>
          <Link to="/contacts/create">Create a Contact</Link>
        </div>

        <Switch>
          <Route exact path="/contacts">
            <ContactList usersData={usersData} viewContactDetails={viewContactDetails} />
          </Route>
          <Route path="/contacts/create">
            <CreateContact createContact={createContact} />
          </Route>
          <Route path="/contacts/:id">
            {selectedContact && (
              <ContactDetails selectedContact={selectedContact} closeContactDetails={closeContactDetails} />
            )}
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
