// App.js
import './App.css';
import { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Link, Routes } from "react-router-dom";

// Import your components here (replace with actual file paths)
import ContactList from "./ContactList"; // Replace with the actual file path
import CreateContact from "./CreateContact"; // Replace with the actual file path
import ContactDetails from "./ContactDetails"; // Replace with the actual file path

function App() {
  const [usersData, setUsersData] = useState([]);
  const [selectedContact, setSelectedContact] = useState(null);

  useEffect(() => {
    getData();
  }, []);

  async function getData() {
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/users');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setUsersData(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  }

  const viewContactDetails = (contact) => {
    setSelectedContact(contact);
  };

  return (
    <Router>
      <div className="App">
        <h1>Menu</h1>
        <div>
          <Link to="/contacts">Show Contact List</Link>
          <Link to="/contacts/create">Create a Contact</Link>
        </div>

        <Routes>
          <Route path="/contacts" element={<ContactList usersData={usersData} />} />
          <Route path="/contacts/create" element={<CreateContact />} />
          <Route path="/contacts/:id" element={<ContactDetails selectedContact={selectedContact} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
