import './App.css';
import { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

function ContactList({ usersData }) {
  return (
    <ul>
      {usersData.map((user) => (
        <li key={user.id}>
          <Link to={`/contacts/${user.id}`}>View</Link>
          {user.name}
        </li>
      ))}
    </ul>
  );
}

function ContactDetails({ selectedContact }) {
  return (
    <div>
      <h2>Contact Details</h2>
      <p>Name: {selectedContact.name}</p>
      <p>Street: {selectedContact.address.street}</p>
      <p>City: {selectedContact.address.city}</p>
      <Link to="/contacts">Back to Contact List</Link>
    </div>
  );
}

function CreateContact() {
  // Your create contact form here
}

function App() {
  const [usersData, setUsersData] = useState([]);
  const [selectedContact, setSelectedContact] = useState(null);

  useEffect(() => {
    getData();
  }, []);

  async function getData() {
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/users');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setUsersData(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  }

  const viewContactDetails = (contact) => {
    setSelectedContact(contact);
  };

  return (
    <Router>
      <div className="App">
        <h1>Menu</h1>
        <div>
          <Link to="/contacts">Show Contact List</Link>
          <Link to="/contacts/create">Create a Contact</Link>
        </div>

        <Route exact path="/contacts">
          <ContactList usersData={usersData} />
        </Route>
        <Route path="/contacts/create">
          <CreateContact />
        </Route>
        <Route path="/contacts/:id">
          {selectedContact && (
            <ContactDetails selectedContact={selectedContact} />
          )}
        </Route>
      </div>
    </Router>
  );
}

export default App;
