import './App.css';
import { useEffect, useState } from "react";
import AddContactForm from "./components/AddContactForm";

function App() {
  const [usersData, setUsersData] = useState([]);
  const [showContactList, setShowContactList] = useState(false);
  const [selectedContact, setSelectedContact] = useState(null);
  const [showAddContactForm, setShowAddContactForm] = useState(false);

  useEffect(() => {
    getData();
  }, []);

  async function getData() {
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/users');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setUsersData(data);
      console.log(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  }

  const addContact = async (newContact) => {
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newContact),
      });

      if (!response.ok) {
        throw new Error('Failed to create contact');
      }

      // Refresh the contact list
      getData();
    } catch (error) {
      console.error('Error creating contact:', error);
    }
  };


  const toggleContactList = () => {
    setShowContactList(!showContactList);
  };

  const viewContactDetails = (contact) => {
    setSelectedContact(contact);
  };

  const closeContactDetails = () => {
    setSelectedContact(null);
  };


  const toggleAddContactForm = () => {
    setShowAddContactForm(!showAddContactForm);
    setShowContactList(false); // Hide the Contact List when showing the Add Contact form
  };


  return (
    <div className="App">
  <h1>Menu</h1>
  <div>
    <a href="#" onClick={toggleContactList}>
      {showContactList ? "Hide Contact List" : "Show Contact List"}
    </a>
  </div>

  <div>
    <a href="#" onClick={toggleAddContactForm}>
      {showAddContactForm ? "Cancel Adding Contact" : "Add a new Contact"}
    </a>
  </div>

  {showAddContactForm && (
    <AddContactForm onAddContact={addContact} />
  )}

  {showContactList && (
    <ul>
      {usersData.map((user) => (
        <li key={user.id}>
          <a href="#" onClick={() => viewContactDetails(user)}>
            View
          </a>
          {user.name}
        </li>
      ))}
    </ul>
  )}

  {selectedContact && (
    <div>
      <h2>Contact Details</h2>
      <p>Name: {selectedContact.name}</p>
      {/* Display additional details from the selectedContact data */}
      <p>Street: {selectedContact.address.street}</p>
      <p>City: {selectedContact.address.city}</p>
      {/* Add more details as needed */}
      <a href="#" onClick={closeContactDetails}>
        Close Contact Details
      </a>
    </div>
  )}
</div>

  );
}

export default App;
