import './App.css';
import { useEffect, useState } from "react";




function App() {
  const [usersData, setUsersData] = useState([]);
  const [showContactList, setShowContactList] = useState(false);
  const [selectedContact, setSelectedContact] = useState(null);


   useEffect(() => {
    getData();
  }, []);

  async function getData() {
    const response = await fetch('https://jsonplaceholder.typicode.com/users')
    const json = await response.json();
    setUsersData(json);
    console.log(json);
  }


  const toggleContactList = () => {
    setShowContactList(!showContactList);
  };

  const viewContactDetails = (contact) => {
    setSelectedContact(contact);
  };


  return (
    <div className="App">
      <h1>Contact Dashboard</h1>
      <ul>
        {usersData.map((contact) => (
          <li key={contact.id}>
            <p>Name: {contact.title}</p>
            <p>Street: {contact.street}</p> 
            <p>City: {contact.city}</p> 
            <a href="#" onClick={() => viewContactDetails(contact)}>
              View
            </a>
          </li>
        ))}
      </ul>
      {selectedContact && (
        <div>
          <h2>Contact Details</h2>
          <p>First Name: {selectedContact.name}</p> 
          <p>Street: {selectedContact.street}</p> 
          <p>City: {selectedContact.city}</p> 
        </div>
      )}
    </div>
  );
}

export default App;
