import React, { useState } from "react";

function AddContactForm({ onAddContact }) {
  const [formData, setFormData] = useState({
    fullName: "", // Combine both first name and last name into a single field
    street: "",
    city: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Split the full name into first name and last name
    const [firstName, lastName] = formData.fullName.split(" ");
    
    // Create a new contact object with separated first name and last name
    const newContact = {
      firstName,
      lastName,
      address: {
        street: formData.street,
        city: formData.city,
      },
    };

    // Call the callback function to add the contact
    onAddContact(newContact);

    // Clear the form fields
    setFormData({
      fullName: "",
      street: "",
      city: "",
    });
  };

  return (
    <div>
      <h2>Create a Contact</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Full Name:
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Street:
          <input
            type="text"
            name="street"
            value={formData.street}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          City:
          <input
            type="text"
            name="city"
            value={formData.city}
            onChange={handleChange}
            required
          />
        </label>
        <button type="submit">Create Contact</button>
      </form>
    </div>
  );
}

export default AddContactForm;
