// App.js (Main component)
import React from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import ContactList from './ContactList';
import ContactDetails from './ContactDetails';
import CreateContactForm from './CreateContactForm';

function App() {

    const navigate = useNavigate()
    const [people, setPeople] = useState([]);
    const [localContact, setLocalContact] = useState([])

    async function getPeople() {
      const response = await fetch("https://jsonplaceholder.typicode.com/users");
      const json = await response.json();
      setPeople(json);
    }

    useEffect(() => {
      getPeople();
    }, []);

    const addContact = (newContact) => {
        setPeople(prevPeople => [...prevPeople, newContact])
        setLocalContact(prevPeople => [...prevPeople, newContact])
    }

    const deleteContact = (id) => {
        setPeople(prevPeople => prevPeople.filter(contact => contact.id !== id))
        navigate('/')
    }
  return (

    <Router>
      <Switch>
        <Route exact path="/" component={ContactList} />
        <Route path="/contact/:id" component={ContactDetails} />
        <Route path="/create-contact" component={CreateContactForm} />
      </Switch>
    </Router>
  );
}

export default App;
