import { useEffect, useState, useContext } from "react"
import { useParams } from "react-router-dom"
import DataContext from "../DataContext"

function ContactPreview(props) {
    const { targetContact, setTargetContact } = useContext(DataContext)
    const { id } = useParams()

    // This effect runs whenever targetContact changes
    useEffect(() => {
        console.log(targetContact); // Debugging: Check the updated targetContact
    }, [targetContact]);

    // Fetch and set the targetContact when id changes
    useEffect(() => {
        const fetchContact = async () => {
            const response = await fetch(`https://jsonplaceholder.typicode.com/users/${id}`);
            const data = await response.json();
            setTargetContact(data);
        };

        fetchContact();
    }, [id, setTargetContact]);

    return (
        <div>
            {targetContact ? (
                <div className="contact-preview">
                    <h1>{targetContact.name}</h1>
                    <ul>
                        <li>City: {targetContact.address.city}</li>
                        <li>Street: {targetContact.address.street}</li>
                        <li>Zipcode: {targetContact.address.zipcode}</li>
                    </ul>
                </div>
            ) : (
                <div>Loading...</div>
            )}
        </div>
    );
}
export default ContactPreview
