import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CreateContact() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    street: '',
    city: '',
  });
  const [formSubmitted, setFormSubmitted] = useState(false); // New state variable

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Log the form data before sending it to the API
      console.log('Form Data:', formData);

      const response = await fetch('https://your-api-url.com/create-contact', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Failed to create a contact');
      }

      // Log a success message after receiving a successful API response
      console.log('Contact created successfully!');

      // Redirect to the contact list page after successful submission
      navigate('/contact-list');
    } catch (error) {
      console.error('Error creating contact:', error);
    }

    // Set the formSubmitted state to true after submission
    setFormSubmitted(true);
  };

  return (
    <div>
      <h2>Create a Contact</h2>
      {formSubmitted ? (
        <p>Contact submitted successfully!</p>
      ) : (
        <form onSubmit={handleSubmit}>
          {/* ... (rest of the form) */}
        </form>
      )}
    </div>
  );
}

export default CreateContact;
