import './App.css';
import { useEffect, useState } from "react";

function App() {
  const [usersData, setUsersData] = useState([]);
  const [showContactList, setShowContactList] = useState(false);
  const [selectedContact, setSelectedContact] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newContact, setNewContact] = useState({
    firstName: "",
    lastName: "",
    street: "",
    city: "",
  });
  const [createdContact, setCreatedContact] = useState(null); // Add state for created contact

  useEffect(() => {
    getData();
  }, []);

  async function getData() {
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/users');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setUsersData(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  }

  const toggleContactList = () => {
    setShowContactList(!showContactList);
  };

  const viewContactDetails = (contact) => {
    setSelectedContact(contact);
  };

  const closeContactDetails = () => {
    setSelectedContact(null);
  };

  const toggleCreateForm = () => {
    setShowCreateForm(!showCreateForm);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewContact({
      ...newContact,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newContact),
      });

      if (!response.ok) {
        throw new Error('Failed to create contact');
      }

      // Assuming the API responds with the created contact data
      const createdContact = await response.json();

      // Add the newly created contact to the list
      setUsersData([...usersData, createdContact]);

      // Set the created contact in the state
      setCreatedContact(createdContact);

      // Reset the form and hide it
      setNewContact({
        firstName: "",
        lastName: "",
        street: "",
        city: "",
      });
      setShowCreateForm(false);
    } catch (error) {
      console.error('Error creating contact:', error);
    }
  };

  return (
    <div className="App">
      <h1>Menu</h1>
      <div>
        <a href="#" onClick={toggleContactList}>
          {showContactList ? "Hide Contact List" : "Show Contact List"}
        </a>
        <a href="#" onClick={toggleCreateForm}>
          Create a Contact
        </a>
      </div>

      {showContactList && (
        <ul>
          {usersData.map((user) => (
            <li key={user.id}>
              <a href="#" onClick={() => viewContactDetails(user)}>
                View
              </a>
              {user.name}
            </li>
          ))}
        </ul>
      )}

      {showCreateForm && (
        <form onSubmit={handleSubmit}>
          <h2>Create Contact</h2>
          <label>
            First Name:





<input
type="text"
name="firstName"
value={newContact.firstName}
onChange={handleInputChange}
/>
</label>
<label>
Last Name:
<input
type="text"
name="lastName"
value={newContact.lastName}
onChange={handleInputChange}
/>
</label>
<label>
Street:

<input
type="text"
name="street"
value={newContact.street}
onChange={handleInputChange}
/>
</label>
<label>
City:
<input
type="text"
name="city"
value={newContact.city}
onChange={handleInputChange}
/>
</label>
<button type="submit">Create</button>
</form>

)}

{selectedContact && selectedContact.address && (
  <div>
    <h2>Contact Details</h2>
    <p>Name: {selectedContact.name}</p>
    <p>Street: {selectedContact.address.street}</p>
    <p>City: {selectedContact.address.city}</p>
    <a href="#" onClick={closeContactDetails}>
      Close Contact Details
    </a>
  </div>
)}
{createdContact && createdContact.address && (
<div>
<h2>Created Contact</h2>
<p>Name: {createdContact.name}</p>
<p>Street: {createdContact.address.street}</p>
<p>City: {createdContact.address.city}</p>
</div>
)}
</div>
);
}

export default App;
